    private void btnProcesar2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesar2ActionPerformed
        // Datos
        double rentabruta = Double.parseDouble(txtIngresosTotales.getText());

        // Proceso
        double RNIT = IngresosTotalesService.CalcularRNIT(rentabruta);
        double IRA = IngresosTotalesService.CalcularImpuestoALaRentaAnual(RNIT);
        double IRM = IngresosTotalesService.CalcularImpuestoALaRentaMensual(IRA);

        //Reporte

        txtRenta.setText(RNIT + "");
        txtImpuestoanual.setText(IRA + "");
        txtImpuestomensual.setText(IRM + "");
    }//GEN-LAST:event_btnProcesar2ActionPerformed

