    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        // Datos
        double sueldomensual = Double.parseDouble(txtSueldoMensual.getText());
        double horasextras = Double.parseDouble(txtHorasExtras.getText());
        double dato = Double.parseDouble(txtAsignacion.getText());
        double dato1 = Double.parseDouble(txtRentaBruta4.getText());
        double dato2 = Double.parseDouble(txtDeduccion.getText());
        double otros = Double.parseDouble(txtOtros.getText());

        //Proceso

        double sueldoanual = ImpuestoALaRentaService.CalcularSueldoAnual(sueldomensual, dato);
        double gratificacion = ImpuestoALaRentaService.CalcularGratificaciones(sueldomensual, dato);
        double gratificacionextraordinaria = ImpuestoALaRentaService.CalcularGratificacionesExtraordinarias(sueldomensual, dato);
        double pagoxhorasextras = ImpuestoALaRentaService.CalcularHorasExtras(horasextras, sueldomensual, dato);
        double rentabruta = ImpuestoALaRentaService.CalcularRentaBruta(sueldomensual, horasextras, otros, dato);
        double RNIT = ImpuestoALaRentaService.CalcularRNIT(rentabruta, dato2, dato1);
        double IRA = ImpuestoALaRentaService.CalcularImpuestoALaRentaAnual(RNIT);
        double IRM =    ImpuestoALaRentaService.CalcularImpuestoALaRentaMensual(IRA);
        double rentanetatotal = ImpuestoALaRentaService.CalcularRentaNeta4(dato1);
        double ingresostotales = ImpuestoALaRentaService.CalcularIngresosTotales(dato1, dato2, sueldomensual, horasextras, otros, dato);
        // Reporte

        txtRepoSueldoAnual.setText(sueldoanual + "");
        txtRepoGratificacion.setText(gratificacion + "");
        txtRepoGratificacionExtraordinaria.setText(gratificacionextraordinaria + "");
        txtRepoPagoxHorasExtras.setText(pagoxhorasextras + "");
        txtRepoRentaBruta.setText(rentabruta + "");
        txtRepoRNIT.setText(RNIT + "");
        txtRepoIRA.setText(IRA + "");
        txtRepoIRM.setText(IRM + "");
        txtRentaNetaTotal.setText(rentanetatotal + "");
        txtRepoIngresosTotal.setText(ingresostotales + "");
        

    }//GEN-LAST:event_btnProcesarActionPerformed

